import React, { useEffect, useState } from 'react';
import Navbar_Manager from '../../../component/Narbar_Manager/Navbar_Manager';
import './Home.css';

const Home_manager = () => {
  const [todaysAppointments, setTodaysAppointments] = useState([]);

  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        const response = await fetch("http://localhost:3121/api/administrative_manager/AppointmentManager");
        if (!response.ok) {
          throw new Error("Failed to fetch appointments");
        }
        const data = await response.json();

        const today = new Date().toISOString().slice(0, 10); // Get today's date in YYYY-MM-DD format

        // Filter appointments for today
        const filteredAppointments = data.filter(appointment => {
          const appointmentDate = new Date(appointment.date).toISOString().slice(0, 10);
          return appointmentDate === today;
        });

        setTodaysAppointments(filteredAppointments);
      } catch (error) {
        console.error("Error fetching appointments:", error);
      }
    };

    fetchAppointments();
  }, []);

  return (
    <div className='div-manager'>
      <Navbar_Manager />
      <div className='content'>
        <h1>المواعيد لليوم</h1>
        {todaysAppointments.length > 0 ? (
          <table className="appointments-table">
            <thead>
              <tr>
                <th>الاسم</th>
                <th>العمر</th>
                <th>وقت الموعد</th>
              </tr>
            </thead>
            <tbody>
              {todaysAppointments.map((appointment, index) => (
                <tr key={index}>
                  <td>{appointment.mother_name}</td>
                  <td>{appointment.ageInMonths}</td>
                  <td>{appointment.Time}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>لا توجد مواعيد لليوم</p>
        )}
      </div>
    </div>
  );
};

export default Home_manager;
