import React, { useState, useEffect } from 'react';

const Statistics = () => {
  const [totalChildren, setTotalChildren] = useState(0);
  const [maleChildren, setMaleChildren] = useState(0);
  const [femaleChildren, setFemaleChildren] = useState(0);
  const [vaccinatedChildren, setVaccinatedChildren] = useState({
    after_birth: 0,
    first_month: 0,
    second_month: 0,
    fourth_month: 0,
    month_six: 0,
    months: 0,
    month: 0,
  });

  useEffect(() => {
    fetch('http://localhost:3121/api/doctor/statistics/totalChildren')
      .then((response) => response.json())
      .then((data) => setTotalChildren(data.totalChildren));

    fetch('http://localhost:3121/api/doctor/statistics/maleChildren')
      .then((response) => response.json())
      .then((data) => setMaleChildren(data.maleChildren));

    fetch('http://localhost:3121/api/doctor/statistics/femaleChildren')
      .then((response) => response.json())
      .then((data) => setFemaleChildren(data.femaleChildren));

    fetch('http://localhost:3121/api/doctor/statistics/vaccinatedChildren')
      .then((response) => response.json())
      .then((data) => setVaccinatedChildren(data));
  }, []);

  return (
    <div>
      <h2>إحصائيات</h2>
      <p>إجمالي عدد الأطفال في العيادة: {totalChildren}</p>
      <p>عدد الأطفال الذكور: {maleChildren}</p>
      <p>عدد الأطفال الإناث: {femaleChildren}</p>
      <h3>عدد الأطفال المطعمين حسب الشهر:</h3>
      <p>بعد الولادة: {vaccinatedChildren.after_birth}</p>
      <p>الشهر الأول: {vaccinatedChildren.first_month}</p>
      <p>الشهر الثاني: {vaccinatedChildren.second_month}</p>
      <p>الشهر الرابع: {vaccinatedChildren.fourth_month}</p>
      <p>الشهر السادس: {vaccinatedChildren.month_six}</p>
      <p>الشهر الثاني عشر: {vaccinatedChildren["12_months"]}</p>
      <p>الشهر الثامن عشر: {vaccinatedChildren["18_month"]}</p>
    </div>
  );
};

export default Statistics;
