import React, { useEffect, useState } from 'react';
import { Bar } from 'react-chartjs-2';
import axios from 'axios';

const generateFakeData = () => {
  return [
    {
      ID: 1,
      date: '2024-01-01',
      age: 1,
      weight: 10,
      height: 70,
      head_circumference: 45,
      vitamin_A_D: 1,
      vitamin_capsule_A: 1,
      Iron_Ascending: 1,
      Child_ID: 1,
    },
    {
      ID: 2,
      date: '2024-02-01',
      age: 2,
      weight: 12,
      height: 80,
      head_circumference: 47,
      vitamin_A_D: 1,
      vitamin_capsule_A: 1,
      Iron_Ascending: 1,
      Child_ID: 1,
    },
    {
      ID: 3,
      date: '2024-03-01',
      age: 3,
      weight: 14,
      height: 90,
      head_circumference: 49,
      vitamin_A_D: 1,
      vitamin_capsule_A: 1,
      Iron_Ascending: 1,
      Child_ID: 1,
    },
    {
      ID: 4,
      date: '2024-04-01',
      age: 4,
      weight: 16,
      height: 100,
      head_circumference: 51,
      vitamin_A_D: 1,
      vitamin_capsule_A: 1,
      Iron_Ascending: 1,
      Child_ID: 1,
    },
    {
      ID: 5,
      date: '2024-05-01',
      age: 5,
      weight: 18,
      height: 110,
      head_circumference: 53,
      vitamin_A_D: 1,
      vitamin_capsule_A: 1,
      Iron_Ascending: 1,
      Child_ID: 1,
    },
  ];
};

const ComparisonChild = () => {
  const [childData, setChildData] = useState([]);
  const [normalChildData, setNormalChildData] = useState({
    age: [1, 2, 3, 4, 5],
    weight: [10, 12, 14, 16, 18],
    height: [70, 80, 90, 100, 110],
    head_circumference: [45, 47, 49, 51, 53],
    vitamin_A_D: [1, 1, 1, 1, 1],
    vitamin_capsule_A: [1, 1, 1, 1, 1],
    Iron_Ascending: [1, 1, 1, 1, 1],
  });

  useEffect(() => {
    // Use fake data initially
    setChildData(generateFakeData());

    // Uncomment the following code to fetch real data when available
    // axios.get('/api/child-data')
    //   .then(response => {
    //     setChildData(response.data);
    //   })
    //   .catch(error => {
    //     console.error('Error fetching child data', error);
    //   });
  }, []);

  const data = {
    labels: childData.map(item => item.age),
    datasets: [
      {
        label: 'Child Weight',
        data: childData.map(item => item.weight),
        backgroundColor: 'rgba(255, 99, 132, 0.2)',
        borderColor: 'rgba(255, 99, 132, 1)',
        borderWidth: 1,
      },
      {
        label: 'Normal Weight',
        data: normalChildData.weight,
        backgroundColor: 'rgba(54, 162, 235, 0.2)',
        borderColor: 'rgba(54, 162, 235, 1)',
        borderWidth: 1,
      },
      {
        label: 'Child Height',
        data: childData.map(item => item.height),
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1,
      },
      {
        label: 'Normal Height',
        data: normalChildData.height,
        backgroundColor: 'rgba(153, 102, 255, 0.2)',
        borderColor: 'rgba(153, 102, 255, 1)',
        borderWidth: 1,
      },
      // Add more datasets for head_circumference, vitamin_A_D, etc.
    ],
  };

  const options = {
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  };

  return (
    <div>
      <h2>Child Growth Comparison</h2>
      <Bar data={data} options={options} />
    </div>
  );
};

export default ComparisonChild;
