import React, { useState, useEffect } from "react";
import { styled } from "@mui/material/styles";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell, { tableCellClasses } from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import "./AppointmentManager.css";

const StyledTableCell = styled(TableCell)(({ theme }) => ({
  [`&.${tableCellClasses.head}`]: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  [`&.${tableCellClasses.body}`]: {
    fontSize: 14,
  },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  "&:nth-of-type(odd)": {
    backgroundColor: theme.palette.action.hover,
  },
  "&:last-child td, &:last-child th": {
    border: 0,
  },
}));

const formatDate = (dateString) => {
  const date = new Date(dateString);
  return new Intl.DateTimeFormat("ar-US", {
    year: "numeric",
    month: "2-digit",
    day: "2-digit",
  }).format(date);
};

const AppointmentManager = () => {
  const [appointments, setAppointments] = useState([]);
  const [oldAppointments, setOldAppointments] = useState([]);
  const [futureAppointments, setFutureAppointments] = useState([]);

  useEffect(() => {
    fetchAppointments();
  }, []);

  useEffect(() => {
    const currentDate = new Date();
    const oldAppts = appointments.filter(
      (appointment) => new Date(appointment.date) < currentDate
    );
    const futureAppts = appointments.filter(
      (appointment) => new Date(appointment.date) >= currentDate
    );

    setOldAppointments(oldAppts);
    setFutureAppointments(futureAppts);
  }, [appointments]);

  const fetchAppointments = async () => {
    try {
      const response = await fetch(
        "http://localhost:3121/api/administrative_manager/AppointmentManager"
      );
      if (!response.ok) {
        throw new Error("Failed to fetch appointments");
      }
      const data = await response.json();
      setAppointments(data);
    } catch (error) {
      console.error("Error fetching appointments:", error);
    }
  };

  return (
    <div>
      <h2>المواعيد القديمة</h2>
      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 700, direction: "rtl" }} aria-label="customized table">
          <TableHead>
            <TableRow>
              <StyledTableCell align="right">اسم الأم</StyledTableCell>
              <StyledTableCell align="right">التاريخ</StyledTableCell>
              <StyledTableCell align="right">الوقت</StyledTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {oldAppointments.map((row) => (
              <StyledTableRow key={row.ID}>
                <StyledTableCell component="th" scope="row" align="right">
                  {row.mother_name}
                </StyledTableCell>
                <StyledTableCell align="right">
                  {formatDate(row.date)}
                </StyledTableCell>
                <StyledTableCell align="right">{row.Time}</StyledTableCell>
              </StyledTableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <h2>المواعيد المستقبلية</h2>
      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 700, direction: "rtl" }} aria-label="customized table">
          <TableHead>
            <TableRow>
              <StyledTableCell align="right">اسم الأم</StyledTableCell>
              <StyledTableCell align="right">التاريخ</StyledTableCell>
              <StyledTableCell align="right">الوقت</StyledTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {futureAppointments.map((row) => (
              <StyledTableRow key={row.ID}>
                <StyledTableCell component="th" scope="row" align="right">
                  {row.mother_name}
                </StyledTableCell>
                <StyledTableCell align="right">
                  {formatDate(row.date)}
                </StyledTableCell>
                <StyledTableCell align="right">{row.Time}</StyledTableCell>
              </StyledTableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default AppointmentManager;
