import React, { useState } from "react";
import { Form, Input, Button, notification } from "antd";
import axios from "axios";
import "./ForgotPassword.css";

const ForgotPassword = () => {
  const [loading, setLoading] = useState(false);
  const [userId, setUserId] = useState("");

  const handleResetPassword = async () => {
    setLoading(true);
    try {
      const response = await axios.post(
        "http://localhost:4154/ForgotPassword",
        { userId }
      );
      notification.success({
        message: "Password Reset",
        description: "تم ارسال كلمة المرور الجديدة الى الهاتف المحمول",
      });
    } catch (error) {
      notification.error({
        message: "Error",
        description:
          error.response?.data.message ||
          "An error occurred while resetting the password",
      });
    }
    setLoading(false);
  };

  return (
    <div className="forgot-password-container">
      <div className="forgot-password-form">
        <div className="forgot-password-header">
          <h2>Reset Password</h2>
        </div>
        <Form name="forgot-password-form" onFinish={handleResetPassword}>
          <Form.Item
            name="userId"
            rules={[{ required: true, message: "Please enter your ID" }]}
          >
            <Input
              onChange={(e) => setUserId(e.target.value)}
              placeholder="ID"
            />
          </Form.Item>
          <Form.Item>
            <Button
              type="primary"
              htmlType="submit"
              loading={loading}
              style={{ width: "100%" }}
            >
              اعادة تعيين كلمة المرور
            </Button>
          </Form.Item>
        </Form>
      </div>
    </div>
  );
};

export default ForgotPassword;
