import React from 'react';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const ComparisonChild = ({ measurements, normalValues }) => {
  const childData = {
    age: measurements.age || [],
    weight: measurements.weight || [],
    height: measurements.height || [],
    head_circumference: measurements.head_circumference || [],
    vitamin_A_D: measurements.vitamin_A_D || [],
    vitamin_capsule_A: measurements.vitamin_capsule_A || [],
    Iron: measurements.Iron || [],
  };

  const data = {
    labels: normalValues.age,
    datasets: [
      {
        label: 'وزن الطفل',
        data: normalValues.age.map(age => childData.age === age ? childData.weight : null),
        backgroundColor: 'rgba(255, 99, 132, 0.2)',
        borderColor: 'rgba(255, 99, 132, 1)',
        borderWidth: 1,
      },
      {
        label: 'الوزن الطبيعي',
        data: normalValues.weight,
        backgroundColor: 'rgba(54, 162, 235, 0.2)',
        borderColor: 'rgba(54, 162, 235, 1)',
        borderWidth: 1,
      },
      {
        label: 'طول الطفل',
        data: normalValues.age.map(age => childData.age === age ? childData.height : null),
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1,
      },
      {
        label: 'الطول الطبيعي',
        data: normalValues.height,
        backgroundColor: 'rgba(153, 102, 255, 0.2)',
        borderColor: 'rgba(153, 102, 255, 1)',
        borderWidth: 1,
      },
      {
        label: 'محيط الراس',
        data: normalValues.age.map(age => childData.age === age ? childData.head_circumference : null),
        backgroundColor: 'rgba(255, 206, 86, 0.2)',
        borderColor: 'rgba(255, 206, 86, 1)',
        borderWidth: 1,
      },
      {
        label: 'محيط الراس الطبيعي',
        data: normalValues.head_circumference,
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1,
      },
    ],
  };

  const options = {
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  };

  return (
    <div>
      <h2>مقارنة نمو الطفل</h2>
      <Bar data={data} options={options} />
    </div>
  );
};

export default ComparisonChild;
